function _colorpicker_manager(){


  this.add = function(){
    $('.colorpicker').remove();
    $('.colorpicker_box').ColorPicker({
      colorpicker: '',
    	onSubmit: function(hsb, hex, rgb, el) {
    		$(el).attr('color',hex);
        $(el).css('background','#'+hex);
    		$(el).ColorPickerHide();
    	},
      onChange: function (hsb, hex, rgb) {
  		  $(colorpicker).attr('color',hex);
        $(colorpicker).css('background', '#' + hex);
      },
    	onBeforeShow: function () {
        colorpicker = this;
    		$(this).ColorPickerSetColor( $(this).attr('color'));
    	}
    });
  }

}
