function _colorpicker_manager(){this.add=function(){$(".colorpicker").remove(),$(".colorpicker_box").ColorPicker({colorpicker:"",onSubmit:function(o,r,c,i){$(i).attr("color",r),$(i).css("background","#"+r),$(i).ColorPickerHide()},onChange:function(o,r){$(colorpicker).attr("color",r),$(colorpicker).css("background","#"+r)},onBeforeShow:function(){colorpicker=this,$(this).ColorPickerSetColor($(this).attr("color"))}})}}